import React, { useEffect, useState, useCallback } from 'react';
// import Header from '../component/Header';
// import Footer from '../component/Footer';
import axios from 'axios';
import Banner from '../Component/Banner';
import Card from '../Component/Card';
import 'antd/dist/reset.css';
import { Pagination } from 'antd';

const Home = () => {
    const [products, setProducts] = useState([]);
    const [categories, setCategories] = useState([]);
    const [category, setCategory] = useState('');
    const [pageNumber, setPageNumber] = useState(1);
    const [count, setCount] = useState(0);

    // Fetch Products
    const fetchProduct = useCallback(() => {
        axios.get(`http://localhost:8080/api/products/all?cat=${category}&pageNumber=${pageNumber}`)
            .then((prods) => {
                setProducts(prods.data.products);
                setCount(prods.data.count);
            })
            .catch(error => {
                console.error("Error fetching products:", error);
            });
    }, [category, pageNumber]);

    // Fetch Categories
    const fetchProductCategory = useCallback(() => {
        axios.get('http://localhost:8080/api/category/all')
            .then((cat) => {
                setCategories(cat.data.categories);
            })
            .catch(error => {
                console.error("Error fetching categories:", error);
            });
    }, []);

    

    useEffect(() => {
        fetchProduct();
    }, [fetchProduct]);

    useEffect(() => {
        fetchProductCategory();
    }, [fetchProductCategory]);

    console.log("Selected Category:", category);

    return (
        <>
            {/* <Header/> */}
            <Banner />
            <div className="container pt-5 pb-5">
                <div className="row">
                    {/* Category Filter Section */}
                    <div className="col-sm-3">
                        <h2>Filter by category</h2>
                        <form>
                            <div className="form-group">
                                <select 
                                    onChange={(e) => setCategory(e.target.value)} 
                                    className='form-control'
                                    value={category}
                                >
                                    <option value="" disabled>Select</option>
                                    {categories && categories.map(cat => (
                                        <option key={cat._id} value={cat._id}>{cat.name}</option>
                                    ))}
                                    <option value="">All</option>
                                </select>
                            </div>
                        </form>
                    </div>

                    {/* Product Display Section */}
                    <div className="col-sm-9">
                        <div className="row">
                            {products && products.length > 0 ? (
                                products.map((p) => (
                                    <Card 
                                        key={p._id} 
                                        image={p.image.url} 
                                        productName={p.name} 
                                        prodLink={`/product/${p._id}`} 
                                        prodCategory={p.category ? p.category.name : ''} 
                                        price={p.price} 
                                    />
                                ))
                            ) : (
                                <p>No products found.</p>
                            )}
                        </div>
                        {/* Pagination */}
                        <Pagination 
                            current={pageNumber} 
                            total={count} 
                            onChange={(page) => setPageNumber(page)} 
                            pageSize={3} 
                        />
                    </div>
                </div>
            </div>
            {/* <Footer/> */}
        </>
    );
};

export default Home;
